function strong_links_query(sn) {
	var url = "http://api.infochimps.com/social/network/tw/graph/strong_links";
	var req = y.rest(url)
						 .accept('application/json')
						 .query("apikey", apikey)
						 .query("screen_name", sn)
						 .get().response;
 return req
}

var twitter = {}

function twitter_list(raw) {
	ary = [];
	for each (var el in raw) {
		ary.push(el.json[0]);
	}
	str = ary.join(",");
	twitter_lookup(str);
	return true;
}

function twitter_lookup(list) {
	var q = "select * from infochimps.twitter where user_id='" + list + "'";
	var req = y.query(q);

	// y.log(typeof req.results)
	// var obj = y.xmlToJson(req.results);
	y.log(req.results)
	for (user in req.results.users) {
		y.log(user)
		if (!user.hasOwnProperty(user)) {
		        //The current property is not a direct property of p
		        continue;
		}
		u = {}
		u.id = user.id;
		u.name = user.name;
		u.location = user.location;

		u.screen_name = u.screen_name;
		eval("twitter._" + user.id + " = " + u);
	}
	return true;
}

function twitter_query(tid) {
	var url = "http://api.twitter.com/1/users/show.json";
	var tw = y.rest(url)
						.query("id", tid)
						.accept('application/json')
						.get().response;
	return tw;
};

function place_query(location) {
	var q = "select * from geo.placefinder where text='" + escape(location) + "'";
	var req = y.query(q);

	return req.results.Result;
};

